/*unordered_map Runtime: 4 ms, faster than 63.75% of C++ online submissions for Map Sum Pairs.Memory Usage: 7.8 MB, less than 52.98% of C++ online submissions for Map Sum Pairs.*/class MapSum {public:        /** Initialize your data structure here. */    MapSum() {           }        void insert(string key, int val) {        mp[key]=val;    }        int sum(string prefix) {        int ans = 0;        for(auto &it: mp){            if(it.first.find(prefix)==0){                ans += it.second;            }        }        return ans;    }private:    unordered_map<string, int> mp;    };